<template>
  <Popup v-model="show" position="bottom" height="auto" :show-mask="true" :hide-on-blur="false" :is-transparent="false" @on-show="onShow" @on-hide="onHide">
    <div class="container">
      <PopupHeader class="header" title="输入数字密码" leftText="取消" @on-click-left="cancel"></PopupHeader>
      <div class="pass-box">
        <div class="err-tip">{{errTip}}</div>
        <ul>
          <li v-for="item in length" :key="item">
            <span v-show="res[item-1]" class="r"></span>
          </li>
        </ul>
      </div>
      <Keyboard ref="Keyboard" type="number" v-model="number" :fixed="false" :maxLength="length+''" confirmText="确认" @confirm="confirm"></Keyboard>
      <div class="loading-box" v-if="loading">
        <div class="loading-box-main">
          <i class="loading"></i>
          <span>验证支付密码</span>
        </div>
      </div>
    </div>
  </Popup>
</template>

<script>
import Popup from '../v-popup'
import PopupHeader from '../v-popup-header'
import { Keyboard, KeyboardInput } from '../v-keyboard'
export default {
  components: { Popup, Keyboard, PopupHeader },
  data() {
    return {
      show: false,
      number: "",
      length: 6,
      errTip: "",
      loading: false,
    }
  },
  computed: {
    res() {
      this.errTip = "";
      return this.number.split("");
    }
  },
  watch: {

  },
  methods: {
    open() {
      this.show = true;
      this.$refs.Keyboard.disorderFn();
    },
    cancel() {
      this.close();
      this.$emit("cancel");
    },
    close() {
      this.show = false;
      this.showLoading(false);
      this.number = "";
    },
    onShow() {
      this.$emit("on-show")
    },
    onHide() {
      this.$emit("on-hide")
    },
    confirm() {
      this.showLoading(true);
      this.$emit("confirm", this.number)
    },
    error(errTip) {
      this.number = "";
      this.showLoading(false);
      this.$refs.Keyboard.disorderFn();
      setTimeout(() => {
        this.errTip = errTip;
      }, 100);
    },
    showLoading(flag) {
      this.loading = Boolean(flag);
    }
  }
}
</script>

